import "./App.css"
import { useState } from "react"
import Movies from "./components/Movies"

export default function App() {
  const [isMoviesOpen, setIsMovieOpen] = useState(false)

  return (
    <div className="App">
      <button className={setIsMovieOpen(!isMoviesOpen)}>Movies</button>
      <div>{isMoviesOpen && <Movies />}</div>
    </div>
  )
}
