import React, { useState } from "react"
import "./Todos.css"

export default function Todos() {
  const [todos, setTodos] = useState([
    { id: 1, title: "study English", isDone: false },
    { id: 2, title: "study Korean", isDone: true },
  ])

  const [inputText, setInputText] = useState("")

  const [todoKey, setTodoKey] = useState(todos.length + 1)

  const render = todos.map((todo) => {
    return (
      <div key={todo.id}>
        <div>
          <span className="todoTitle" onClick={() => modTodo(todo.id)}>
            #{todo.id} / {todo.title}
          </span>
          <span className="todoIsDone" onClick={() => modTodo(todo.id)}>
            {todo.isDone ? "DONE✅" : "TODO"}
          </span>
          <button className="todoBtn" onClick={() => delTodo(todo.id)}>
            ❎
          </button>
        </div>
      </div>
    )
  })

  const addTodo = (inputText) => {
    const addItem = [...todos, { id: todoKey, title: inputText }]
    setTodoKey(todoKey + 1)
    setTodos(addItem)
    setInputText("")
  }

  const delTodo = (id) => {
    const delItem = todos.filter((item) => {
      return item.id !== id
    })
    setTodos(delItem)
  }

  const modTodo = (id) => {
    const modItem = todos.map((item) => {
      return item.id === id ? { ...item, isDone: !item.isDone } : item
    })
    setTodos(modItem)
  }

  return (
    <div>
      <h1>Todos</h1>
      <div>
        <input
          type="text"
          placeholder="New todo"
          value={inputText}
          onChange={(e) => {
            setInputText(e.target.value)
          }}
        ></input>
        <button onClick={() => addTodo(inputText)}>SEND</button>
      </div>
      <div>{render}</div>
    </div>
  )
}
