import React, { useState } from "react"

export default function Todos() {
  const [todos, setTodos] = useState([
    { id: 1, title: "study English" },
    { id: 2, title: "study Korean" },
  ])

  const [inputText, setInputText] = useState("")

  const [todoKey, setTodoKey] = useState(todos.length + 1)

  const render = todos.map((todo) => {
    return (
      <div key={todo.id}>
        <div>
          <span>
            #{todo.id} / {todo.title}
          </span>
          <button onClick={() => delTodo()}>‚ùé</button>
        </div>
      </div>
    )
  })

  const addTodo = (inputText) => {
    const addItem = [...todos, { id: todoKey, title: inputText }]
    setTodoKey(todoKey + 1)
    setTodos(addItem)
    setInputText("")
  }

  const delTodo = () => {
    alert("Del Todo!")
  }
  // const delArr = arr.filter((item, index, arr) => {
  //   return index !== arr.length - 1
  // })
  // console.log(delArr)

  return (
    <div>
      <h1>Todos</h1>
      <div>
        <input
          type="text"
          placeholder="New todo"
          value={inputText}
          onChange={(e) => {
            setInputText(e.target.value)
          }}
        ></input>
        <button onClick={() => addTodo(inputText)}>SEND</button>
      </div>
      <div>{render}</div>
    </div>
  )
}
